import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { Row, Col, Image, ListGroup, Button, Card, Container } from "react-bootstrap";
import Rating from "../Rating";
import axios from "axios";
import Footer from "../Footer"; // Import Footer component

// Import movies_data function from appropriate file
import movies_data from "../fetchmoviesdata";

function ProductScreen() {
  const { id } = useParams();
  const [rating, setRating] = useState(0); // State to hold the user's rating
  const [movies, setMovies] = useState([]); // State to hold the movies data

  useEffect(() => {
    // Fetch movies data
    const movies = movies_data();
    setMovies(movies);
  }, []);

  // Function to retrieve movie image by ID
  const getMovieImage = (movies, movieId) => {
    const movie = movies.find(movie => movie.id === parseInt(movieId));
    return movie ? movie.image_url : null;
  };

  // Get movie image
  const movieImage = getMovieImage(movies,4);

  // Check if movies data is available
  console.log("Movies data:", movies);
  console.log("Movies data length:", movies.length);

  // Handle case when movie image is not available
  const renderMovieImage = () => {
    if (!movieImage) {
      return (
        <div>No image available</div>
      );
    }
    return <Image src={movieImage} alt="Movie" fluid />;
  };

  // Function to handle submitting the rating
  const submitRating = async () => {
    try {
      // Get the access token from local storage
      const accessToken = localStorage.getItem('accessToken');

      // Send the rating data to the backend along with the JWT token
      await axios.post(`/submit_rating/${id}`, { rating }, {
        headers: {
          Authorization: `Bearer ${accessToken}`
        }
      });

      // Optionally, you can update the UI or show a confirmation message
      console.log("Rating submitted successfully!");
    } catch (error) {
      // Handle error
      console.error("Error submitting rating:", error);
    }
  };

  return (
    <Container>
      <div>
        <Link to="/" className="btn btn-dark my-3">
          Go Back
        </Link>

        <Row>
          <Col md={6}>
            {renderMovieImage()}
          </Col>

          <Col md={3}>
            <ListGroup variant="flush">
              <ListGroup.Item>
                <h3>{/* You should provide the title here */}</h3>
              </ListGroup.Item>
              <ListGroup.Item>
                <Rating value={rating} onChange={(value) => setRating(value)} />
                {/* Display the Rating component with a value and onChange handler */}
              </ListGroup.Item>
              <ListGroup.Item>
                <Button onClick={submitRating} className="btn-block" disabled={rating === 0}>
                  Submit Rating
                </Button>
                {/* Button to submit the rating, disabled if no rating is selected */}
              </ListGroup.Item>
            </ListGroup>
          </Col>
        </Row>
      </div>
      <Footer /> {/* Include Footer component here */}
    </Container>
  );
}

export default ProductScreen;
