import React, { useState } from 'react';
import { Card, ListGroup, Form, Alert } from "react-bootstrap";
import axios from 'axios'; // Import axios for HTTP requests
import { Link } from 'react-router-dom';


function Mylistsscreen({ product }) {
    const [error, setError] = useState("");
    const [successMessage, setSuccessMessage] = useState("");

    const handleDelete = async () => {
        try {
            const accessToken = localStorage.getItem("accessToken");
            const response = await axios.delete(
                `/mylist/`, // Specify the movie ID to delete
                {
                    movie: '${product.title}',
                }, 
                {
                    headers: {
                        Authorization: `Bearer ${accessToken}`,
                    },
                }
            );

            const { message } = response.data;

            if (response.status === 200) {
                setSuccessMessage(message);
                setError("");
            } else {
                setError("Error deleting. Please try again later.");
                setSuccessMessage("");
            }
        } catch (error) {
            console.error("Error deleting movie:", error);
            setError("An error occurred. Please try again later.");
            setSuccessMessage("");
        }
    };

    return (
        <Card className='my-3 p-3 rounded'>

                {successMessage && <Alert variant="success">{successMessage}</Alert>}
                {error && <Alert variant="danger">{error}</Alert>}

            <Link to={`/movies/${product.id}/`}>
                <Card.Img src={product.image_url} />
            </Link>
            <Card.Body>
                <Link to={`/movies/${product.id}/`} className='text-dark'>
                    <Card.Title as="h3">
                        <Link to={`/movies/${product.id}/`} className='text-dark'>
                            {product.title}
                        </Link>
                    </Card.Title>
                </Link>
                <ListGroup variant="flush">
                    <ListGroup.Item>
                        <Form.Check
                            type="checkbox"
                            label="Delete the movie from MyList"
                            onChange={handleDelete} // Call handleDelete function on checkbox change
                        />
                    </ListGroup.Item>
                </ListGroup>

            </Card.Body>
        </Card>
    );
}

export default Mylistsscreen;
