// Import React, useState, useEffect, useParams
import React, { useState } from "react";
import { Link, useParams } from "react-router-dom";
import { Row, Col, Image, ListGroup, Button, Container } from "react-bootstrap";
import Rating from "../Rating";
import axios from "axios";
import Footer from "../Footer"; // Import Footer component

// Import movies_data function from appropriate file
import movies_data from "../fetchmoviesdata";

function ProductScreen() {
  const { id } = useParams();
  const [rating, setRating] = useState(0); // State to hold the user's rating

  const moviesData = movies_data();

  // Function to retrieve movie image URL by ID
  const getMovieImageURL = (movieId) => {
    const movie = moviesData.find(movie => movie.id === parseInt(movieId));
    return movie ? movie.image_url : null;
  };

  const getMovieTitle = (movieId) => {
    const movie = moviesData.find(movie => movie.id === parseInt(movieId));
    return movie ? movie.title : null;
  };

  // Usage example
  const movieImageURL = getMovieImageURL(id); // Output the movie image URL or null if not found
  const movieTitle = getMovieTitle(id);

  // Function to handle submitting the rating
  const submitRating = async () => {
    try {
      // Get the access token from local storage
      const accessToken = localStorage.getItem('accessToken');

      // Send the rating data to the backend along with the JWT token
      const response = await axios.post(
        `/rating/`,
        {
          movie: movieTitle,
          rating: rating // Ensure the rating value is included
        },
        {
          headers: {
            Authorization: `Bearer ${accessToken}`
          }
        }
      );

      // Check if the rating was successfully added
      if (response.status === 201) {
        // Show success message to the user
        alert('Rating added successfully!');
      } else {
        // Show error message to the user
        alert('Error adding rating. Please try again later.');
      }
    } catch (error) {
      // Handle error
      console.error("Error submitting rating:", error);
      // Show error message to the user
      alert('Error adding rating. Please try again later.');
    }
  };

  return (
    <Container>
      <div>
        <Link to="/" className="btn btn-dark my-3">
          Go Back
        </Link>

        <Row>
          <h1>{movieTitle}</h1>

          <Col md={6}>
            <Image src={movieImageURL} fluid />
          </Col>

          <Col md={3}>
            <ListGroup variant="flush">
              <ListGroup.Item>
                <Rating value={rating} onChange={(value) => setRating(value)} />
                {/* Display the Rating component with a value and onChange handler */}
              </ListGroup.Item>
              <ListGroup.Item>
                <Button onClick={() => submitRating()} className="btn-block" disabled={rating === 0}>
                  Submit Rating
                </Button>
                {/* Button to submit the rating, disabled if no rating is selected */}
              </ListGroup.Item>
            </ListGroup>
          </Col>
        </Row>
      </div>
      <Footer /> {/* Include Footer component here */}
    </Container>
  );
}

export default ProductScreen;
