import { useState, useEffect } from "react";
import axios from "axios";

const useProducts = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        // Uncomment this section if you need to include an access token
        // const accessToken = localStorage.getItem('accessToken');
        // const { data } = await axios.get("/movies/", {
        //   headers: {
        //     Authorization: `Bearer ${accessToken}`
        //   }
        // });

        // Make sure the endpoint is correct and returns the expected movie data
        const response = await axios.get("/movies/"); // Changed variable name to response
        setProducts(response.data); // Changed to response.data
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };
    fetchProducts();
  }, []); // Removed dependency array to prevent unnecessary refetching

  return products;
};

export default useProducts;
