import React, { useState } from "react";
import { Link, useParams } from "react-router-dom";
import { Container } from "react-bootstrap";
import axios from "axios";
import Footer from "../Footer";
import movies_data from "../fetchmoviesdata";
import MovieDetails from "./MovieDetails";

function ProductScreen() {
  const { id } = useParams();
  const moviesData = movies_data();
  const [rating, setRating] = useState(0);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [movieTitle, setMovieTitle] = useState(""); // State to hold the movie title

  const getMovieImageURL = (movieId) => {
    const movie = moviesData.find((movie) => movie.id === parseInt(movieId));
    return movie ? movie.image_url : null;
  };

  const getMovieTitle = (movieId) => {
    const movie = moviesData.find((movie) => movie.id === parseInt(movieId));
    return movie ? movie.title : null;
  };

  const movieImageURL = getMovieImageURL(id);
  const currentMovieTitle = getMovieTitle(id);

  const handleCheckboxChange = (e) => {
    const { checked } = e.target;
    if (checked) {
      setMovieTitle(currentMovieTitle);
      addToMyList(); // Call addToMyList when the checkbox is checked
    } else {
      setMovieTitle(""); // Reset movie title if checkbox is unchecked
    }
  };

  // Your submitRating and addToMyList functions remain the same

  return (
    <Container>
      <br />
      <div>
        {error && <div className="alert alert-danger">{error}</div>}
        {successMessage && <div className="alert alert-success">{successMessage}</div>}
      </div>
      <div>
        <Link to="/" className="btn btn-dark my-3">
          Go Back
        </Link>

        <MovieDetails 
          movieImageURL={movieImageURL} 
          currentMovieTitle={currentMovieTitle} 
          rating={rating} 
          setRating={setRating} 
          submitRating={submitRating} 
          handleCheckboxChange={handleCheckboxChange} 
        />

      </div>
      <Footer />
    </Container>
  );
}

export default ProductScreen;
