import React, { useEffect, useState } from "react";
import { LinkContainer } from "react-router-bootstrap";
import { Container,Nav } from "react-bootstrap";
import { Routes, Route, Link } from 'react-router-dom';
import fetchMoviesData from "./fetchmoviesdat";


import Footer from "./Footer";




function MyList() {

  const moviesdata  = fetchMoviesData;
  const [successToken, setSuccessToken] = useState(""); // Corrected variable name
  const [movies, setMovies] = useState([]);


  useEffect(() => {
    const accessToken = localStorage.getItem("accessToken");

    if (accessToken) {
      // Set the success token value
      setSuccessToken(accessToken);
    } else {
      // Display message to the user if not logged in
      console.log("You haven't been logged in.");
    }
  }, []);

  useEffect(() => {
    const getMovies = async () => {
      const data = await fetchMoviesData();
      setMovies(data);
    };

    getMovies();
  }, []);

  return (
    <div className="text-center mt-5">
      {successToken ? (
        <>
      <div>
      <h2>Manage Movies</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Genre</th>
            <th>URL</th>
          </tr>
        </thead>
        <tbody>
          {movies.map(movie => (
            <tr key={movie.id}>
              <td>{movie.name}</td>
              <td>{movie.genre}</td>
              <td>
                <a href={movie.url} target="_blank" rel="noopener noreferrer">
                  {movie.url}
                </a>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
        </>
      ) : (
        <>
          <h2>
             You are not authourised to use You have to be a  registered Admin and logged-in .
          </h2>
          <LinkContainer to="/login">
            <Nav.Link className="navbar-link active">
              <h5> Click Here To Login </h5>
            </Nav.Link>
          </LinkContainer>
        </>
      )}
    </div>
  );
}

export default MyList;
