import React from "react";
import { Row, Col, Image, ListGroup, Button, Form } from "react-bootstrap";
import Rating from "../Rating";

const MovieDetails = ({ movieImageURL, currentMovieTitle, rating, setRating, submitRating, handleCheckboxChange }) => {
  return (
    <Row>
      <h1>{currentMovieTitle}</h1>

      <Col md={6}>
        <Image src={movieImageURL} fluid />
      </Col>

      <Col md={3}>
        <ListGroup variant="flush">
          <ListGroup.Item>
            <Rating value={rating} onChange={(value) => setRating(value)} />
          </ListGroup.Item>
          <ListGroup.Item>
            <Button onClick={submitRating} className="btn-block" disabled={rating === 0}>
              Submit Rating
            </Button>
          </ListGroup.Item>
          <ListGroup.Item>
            <Form.Check
              type="checkbox"
              label="Add to My List"
              onChange={handleCheckboxChange}
            />
          </ListGroup.Item>
        </ListGroup>
      </Col>
    </Row>
  );
};

export default MovieDetails;
