import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

function LoginScreen() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    repeatPassword: ''
  });

  const history = useHistory(); // History instance for redirection

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.id]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send form data to backend server
      const response = await fetch('/login/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      const data = await response.json();

      if (response.ok) {
        // Authentication successful
        const token = data.token; // Assuming your backend returns a JWT token
        // Save the token to localStorage or sessionStorage
        localStorage.setItem('token', token);
        // Redirect user to another page
        history.push('/'); // Replace '/dashboard' with your desired page URL
      } else {
        // Handle authentication failure
        console.error('Authentication failed:', data.error); // Log the error or handle it appropriately
      }
    } catch (error) {
      console.error('Error:', error); // Log any network or other errors
    }
  };

  return (
    <section className="vh-100" style={{ backgroundColor: '#eee' }}>
      <div className="container h-100">
        <div className="row d-flex justify-content-center align-items-center h-100">
          <div className="col-lg-12 col-xl-11">
            <div className="card text-black" style={{ borderRadius: '25px' }}>
              <div className="card-body p-md-5">
                <div className="row justify-content-center">
                  <div className="col-md-10 col-lg-6 col-xl-5 order-2 order-lg-1">
                    <p className="text-center h1 fw-bold mb-5 mx-1 mx-md-4 mt-4">Sign up</p>
                    <form className="mx-1 mx-md-4" onSubmit={handleSubmit}>
                      <div className="d-flex flex-row align-items-center mb-4">
                        <i className="fas fa-user fa-lg me-3 fa-fw"></i>
                        <div data-mdb-input-init className="form-outline flex-fill mb-0">
                          <input type="text" id="name" className="form-control" value={formData.name} onChange={handleChange} />
                          <label className="form-label" htmlFor="name">Your Name</label>
                        </div>
                      </div>
                      {/* Repeat similar structure for other form fields */}
                      <div className="d-flex justify-content-center mx-4 mb-3 mb-lg-4">
                        <button type="submit" data-mdb-button-init data-mdb-ripple-init className="btn btn-primary btn-lg">Register</button>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

export default LoginScreen;
