import React, { useState } from "react";
import { Link, useParams } from "react-router-dom";
import { Row, Col, Image, ListGroup, Button, Container, Form } from "react-bootstrap";
import Rating from "../Rating";
import axios from "axios";
import Footer from "../Footer";
import movies_data from "../fetchmoviesdata";

function ProductScreen() {
  const { id } = useParams();
  const [rating, setRating] = useState(0);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [addToMyList, setAddToMyList] = useState(false); // State to track whether the movie is added to "My List"

  const moviesData = movies_data();

  const getMovieImageURL = (movieId) => {
    const movie = moviesData.find((movie) => movie.id === parseInt(movieId));
    return movie ? movie.image_url : null;
  };

  const getMovieTitle = (movieId) => {
    const movie = moviesData.find((movie) => movie.id === parseInt(movieId));
    return movie ? movie.title : null;
  };

  const movieImageURL = getMovieImageURL(id);
  const movieTitle = getMovieTitle(id);

  const submitRating = async () => {
    try {
      const accessToken = localStorage.getItem("accessToken");
      const response = await axios.post(
        `/rating/`,
        {
          movie: movieTitle,
          rating: rating,
        },
        {
          headers: {
            Authorization: `Bearer ${accessToken}`,
          },
        }
      );

      const { message } = response.data;

      if (response.status === 201) {
        setSuccessMessage(message);
        setError("");
      } else if (response.status === 401) {
        setSuccessMessage("");
        setError("Authentication credentials were not provided.");
      }

    } catch (error) {
      console.error("Error submitting rating:", error);
      setError("Movie rating has already been added.");
      setSuccessMessage("");
    }
  };

  const handleAddToMyListChange = () => {
    setAddToMyList(!addToMyList);
  };

  return (
    <Container>
      <br />
      <div>
        {error && <div className="alert alert-danger">{error}</div>}
        {successMessage && <div className="alert alert-success">{successMessage}</div>}
      </div>
      <div>
        <Link to="/" className="btn btn-dark my-3">
          Go Back
        </Link>

        <Row>
          <h1>{movieTitle}</h1>

          <Col md={6}>
            <Image src={movieImageURL} fluid />
          </Col>

          <Col md={3}>
            <ListGroup variant="flush">
              <ListGroup.Item>
                <Rating value={rating} onChange={(value) => setRating(value)} />
              </ListGroup.Item>
              <ListGroup.Item>
                <Button onClick={submitRating} className="btn-block" disabled={rating === 0}>
                  Submit Rating
                </Button>
              </ListGroup.Item>
              <ListGroup.Item>
                <Form.Check
                  type="checkbox"
                  label="Add to My List"
                  checked={addToMyList}
                  onChange={handleAddToMyListChange}
                />
              </ListGroup.Item>
            </ListGroup>
          </Col>
        </Row>
      </div>
      <Footer />
    </Container>
  );
}

export default ProductScreen;
