import React, { useEffect, useState } from "react";
import axios from "axios"; // Import axios for making API requests

function MovieDetails({ moviename }) {
    const [movieDetails, setMovieDetails] = useState(null);

    useEffect(() => {
        const API_KEY = "d61ae016cfddbd20b6e8194e558cc4d8";

        const fetchMovieDetails = async () => {
            try {
                const response = await axios.get('https://api.themoviedb.org/3/search/movie?query=${moviename}&api_key=${API_KEY}&language=en-US') ;
                setMovieDetails(response.data); // Set movieDetails with response.data
            } catch (error) {
                console.error("Error fetching movie details:", error);
            }
        };

        fetchMovieDetails();

    }, [movieId]); // Add movieId to dependency array to re-fetch details when it changes

    return movieDetails; // Return movieDetails directly
}

export default MovieDetails;
