import React from "react";
import Header from "./components/Header";
import { HashRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import HomeScreen from "./components/screens/HomeScreen";
import SignupScreen from "./components/screens/SignupScreen";
import LoginScreen from "./components/screens/LoginScreen";
import Logout from "./components/screens/Logout";
import ProductScreen from "./components/screens/ProductScreen";
import MyList from "./components/Mylist";
import MyRateings from "./components/Myratings";
import Myrecommendation from "./components/recommendation";
import Adminlogin from "./components/adminlogin";

export default function App() {
  // Get current location
  const location = useLocation();

  // Check if current location is '/Admin'
  const isAdminRoute = location.pathname === "/Admin";

  return (
    <>
      <Router>
        {/* Conditionally render Header */}
        {!isAdminRoute && <Header />}

        <Routes>
          <Route exact path="/" element={<HomeScreen />} />
          <Route exact path="/login" element={<LoginScreen />} />
          <Route exact path="/signup" element={<SignupScreen />} />
          <Route exact path="/logout" element={<Logout />} />
          <Route exact path="/movies/:id" element={<ProductScreen />} />
          <Route exact path="/MyRatings" element={<MyRateings />} />
          <Route exact path="/mylist" element={<MyList />} />
          <Route exact path="/GetRecommendation" element={<Myrecommendation />} />
          {/* Route for Adminlogin */}
          <Route exact path="/Admin" element={<Adminlogin />} />
        </Routes>
      </Router>
    </>
  );
}
