import React, { useEffect, useState } from "react";
import { LinkContainer } from "react-router-bootstrap";
import { Container, Nav } from "react-bootstrap";
import fetchMoviesData from "./fetchmoviesdata";
import Footer from "./Footer";

function MyList() {
  const [movies, setMovies] = useState([]); // Initialize movies as an empty array
  const [successToken, setSuccessToken] = useState("");

  useEffect(() => {
    const accessToken = localStorage.getItem("accessToken");

    if (accessToken) {
      setSuccessToken(accessToken);
    } else {
      console.log("You haven't been logged in.");
    }

    const fetchMovies = async () => {
      try {
        const data = await fetchMoviesData(); // Await the result of fetchMoviesData
        setMovies(data); // Set movies with the fetched data
      } catch (error) {
        console.error("Error fetching movies:", error);
      }
    };

    fetchMovies();
  }, []);

  return (
    <div className="text-center mt-5">
      {successToken ? (
        <>
          <div>
            <h2>Manage Movies</h2>
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Genre</th>
                  <th>URL</th>
                </tr>
              </thead>
              <tbody>
                {movies.map((movie) => (
                  <tr key={movie.id}>
                    <td>{movie.title}</td>
                    <td>{movie.genre}</td>
                    <td>
                      <a
                        href={movie.image_url}
                        target="_blank"
                        rel="noopener noreferrer"
                      >
                        {movie.image_url}
                      </a>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      ) : (
        <>
          <h2>You are not authorized to use this feature. You have to be a registered Admin and logged-in.</h2>
          <LinkContainer to="/login">
            <Nav.Link className="navbar-link active">
              <h5> Click Here To Login </h5>
            </Nav.Link>
          </LinkContainer>
        </>
      )}
      <Footer />
    </div>
  );
}

export default MyList;
