import React, { useEffect, useState } from "react";
import { LinkContainer } from "react-router-bootstrap";
import { Container, Nav, Col, Button, Modal, Form, Alert } from "react-bootstrap"; // Import Alert from react-bootstrap
import { Routes, Route, Link } from 'react-router-dom';
import fetchMoviesData from "./fetchmoviesdata";
import Footer from "./Footer";


function MyList() {
  const movies = fetchMoviesData();
  const [successToken, setSuccessToken] = useState("");
  const [showAddMovieModal, setShowAddMovieModal] = useState(false); // State to manage the visibility of the Add Movie form
  const accessToken = localStorage.getItem("accessToken");
  const [formValues, setFormValues] = useState({ // State to manage form values
    movieName: "",
    movieGenre: "",
    movieURL: ""
  });
  const [submitMessage, setSubmitMessage] = useState({ // State to manage submit messages
    variant: "success", // Default to success
    message: ""
  });

  useEffect(() => {
    if (accessToken) {
      setSuccessToken(accessToken);
    } else {
      console.log("You haven't been logged in.");
    }
  }, []);

  const handleShowAddMovieModal = () => setShowAddMovieModal(true);
  const handleCloseAddMovieModal = () => setShowAddMovieModal(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("/adminmanage_movies/", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${accessToken}`,
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          title: formValues.movieName,
          genre: formValues.movieGenre,
          image_url: formValues.movieURL
        })
      });

      if (response.ok) {
        setSubmitMessage({
          variant: "success",
          message: "Movie added successfully."
        });

        // Clear success message after 3 seconds
        setTimeout(() => {
          setSubmitMessage({ variant: "success", message: "" });
        }, 3000);

        // Clear form values 
        setFormValues({
          movieName: "",
          movieGenre: "",
          movieURL: ""
        });
      } else {
        setSubmitMessage({
          variant: "danger",
          message: "Failed to add movie. Please try again later."
        });
      }
    } catch (error) {
      setSubmitMessage({
        variant: "danger",
        message: "An error occurred. Please try again later."
      });
    }
  };

  return (
    <div className="text-center mt-5">
      {successToken ? (
        <div>


      {/* Success/Error message */}
      {submitMessage.message && (
        <Alert variant={submitMessage.variant}>
          {submitMessage.message}
        </Alert>
      )}

          <h2>Manage Movies</h2>

          {/* Add Movie button */}
          <Button variant="primary" onClick={handleShowAddMovieModal}>Add Movie</Button>

          <table className="table table-borderless table-dark">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">Name</th>
                <th scope="col">Genre</th>
                <th scope="col">URL</th>
                <th scope="col">Action</th>
              </tr>
            </thead>
            <tbody> 
              {movies.map((movie, index) => (
                <tr key={movie._id}>
                  <th scope="row">{index + 1}</th>
                  <td>{movie.title}</td>
                  <td>{movie.genre}</td>
                  <td>
                    <a href={movie.image_url} target="_blank" rel="noopener noreferrer">
                      {movie.image_url.split("/").slice(-2)[0]}
                    </a>
                  </td>
                  <td>
                    <a href="" target="_blank" rel="noopener noreferrer">
                      <button type="button" className="btn btn-outline-success">UPDATE</button>
                    </a> 
                    <a href="" target="_blank" rel="noopener noreferrer">
                      <button type="button" className="btn btn-outline-danger">DELETE</button>
                    </a>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <>
          <h2>
            You are not authorized to use. You have to be a registered Admin and logged-in.
          </h2>
          <LinkContainer to="/login">
            <Nav.Link className="navbar-link active">
              <h5> Click Here To Login </h5>
            </Nav.Link>
          </LinkContainer>
        </>
      )}

      {/* Modal for Add Movie form */}
      <Modal show={showAddMovieModal} onHide={handleCloseAddMovieModal}>
        <Modal.Header closeButton>
          <Modal.Title>Add Movie</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="formMovieName">
              <Form.Label>Movie Name</Form.Label>
              <Form.Control type="text" placeholder="Enter movie name" name="movieName" value={formValues.movieName} onChange={handleInputChange} />
            </Form.Group>
            <Form.Group controlId="formMovieGenre">
              <Form.Label>Genre</Form.Label>
              <Form.Control type="text" placeholder="Enter movie genre" name="movieGenre" value={formValues.movieGenre} onChange={handleInputChange} />
            </Form.Group>
            <Form.Group controlId="formMovieURL">
              <Form.Label>Image URL</Form.Label>
              <Form.Control type="text" placeholder="Enter image URL" name="movieURL" value={formValues.movieURL} onChange={handleInputChange} />
            </Form.Group>
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form>
        </Modal.Body>
      </Modal>


      <Footer />
    </div>
  );
}

export default MyList;
