import React, { useEffect, useState } from "react";
import { Link, useParams } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Button, Card, Container } from 'react-bootstrap';
import Rating from '../Rating';
import axios from "axios";



function ProductScreen() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [rating, setRating] = useState(0);

  useEffect(() => {
    async function fetchProduct() {
      try {
        const { data } = await axios.get(`/spefic_movies/${id}/`);
        setProduct(data);
      } catch (error) {
        console.error('Error fetching product:', error);
      }
    }

    fetchProduct();
  }, [id]);

  const handleRatingChange = (value) => {
    setRating(value);
  };

  const handleRatingSubmit = async () => {
    try {
      const response = await axios.post(`/rate_movie/${id}/`, { rating });
      console.log('Rating submitted:', response.data);
      // You can add further logic here, like updating the UI after successful submission
    } catch (error) {
      console.error('Error submitting rating:', error);
    }
  };

  return (
    <Container>
      <div>
        <Link to="/" className="btn btn-dark my-3">
          Go Back
        </Link>

        {product && (
          <Row>
            <Col md={6}>
              <Image src={product.image_url} alt={product.title} fluid />
            </Col>
            <Col md={3}>
              <ListGroup variant="flush">
                <ListGroup.Item>
                  <h3>{product.title}</h3>
                </ListGroup.Item>
                <ListGroup.Item>
                  <Rating
                    value={product.rating}
                    text={`${product.numReviews} reviews`}
                    color={"#f8e825"}
                    onRatingChange={handleRatingChange}
                  />
                  <Button onClick={handleRatingSubmit}>Submit Rating</Button>
                </ListGroup.Item>
                <ListGroup.Item>Genre: {product.genre}</ListGroup.Item>
              </ListGroup>
            </Col>
          </Row>
        )}
      </div>
    </Container>
  );
}

export default ProductScreen;
