import React, { useEffect, useState } from "react";
import { LinkContainer } from "react-router-bootstrap";
import { Container, Nav, Button, Modal, Form, Alert, Table } from "react-bootstrap";
import axios from "axios";
import Footer from "./Footer";

function MyList() {
  const [movies, setMovies] = useState([]);
  const [successToken, setSuccessToken] = useState("");
  const [showAddMovieModal, setShowAddMovieModal] = useState(false);
  const [accessToken, setAccessToken] = useState(localStorage.getItem("accessToken") || "");
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [formValues, setFormValues] = useState({
    movieName: "",
    movieGenre: "",
    movieURL: ""
  });
  const [submitMessage, setSubmitMessage] = useState({
    variant: "success",
    message: ""
  });

  useEffect(() => {
    if (accessToken) {
      setSuccessToken(accessToken);
    } else {
      console.log("You haven't been logged in.");
    }
  }, [accessToken]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get("/adminmanage_movies");
        setMovies(response.data);
      } catch (error) {
        console.error("Error fetching movies:", error);
      }
    }

    fetchData();
  }, []); // Empty dependency array to fetch movies only once

  const handleShowAddMovieModal = () => setShowAddMovieModal(true);
  const handleCloseAddMovieModal = () => setShowAddMovieModal(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleAddMovieSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("/adminmanage_movies/", formValues, {
        headers: {
          Authorization: `Bearer ${accessToken}`,
          "Content-Type": "application/json"
        }
      });

      if (response.status === 200) {
        setSubmitMessage({
          variant: "success",
          message: "Movie added successfully."
        });

        setTimeout(() => {
          setSubmitMessage({ variant: "success", message: "" });
        }, 5000);

        setFormValues({
          movieName: "",
          movieGenre: "",
          movieURL: ""
        });

        // Refetch movies after adding a new one
        const updatedMovies = await axios.get("/adminmanage_movies");
        setMovies(updatedMovies.data);
      } else {
        setSubmitMessage({
          variant: "danger",
          message: "Failed to add movie. Please try again later."
        });
      }
    } catch (error) {
      console.error("Error adding movie:", error);
      setSubmitMessage({
        variant: "danger",
        message: "An error occurred. Please try again later."
      });
    }
  };


  const handleDeleteMovie = async (movieId) => {
    try {
      const response = await axios.delete(`/adminmanage_movies/${movieId}`, {
        headers: {
          Authorization: `Bearer ${accessToken}`,
          "Content-Type": "application/json"
        }
      });

      if (response.status === 200) {
        setSuccessMessage("Movie deleted successfully.");
        // Refetch movies after deleting one
        const updatedMovies = await axios.get("/adminmanage_movies");
        setMovies(updatedMovies.data);
      } else {
        setError("Failed to delete movie. Please try again later.");
      }
    } catch (error) {
      console.error("Error deleting movie:", error);
      setError("An error occurred. Please try again later.");
    }
  };


  return (
    <Container className="text-center mt-5">
      {successToken ? (
        <>
          <h2>Manage Movies</h2>
          {submitMessage.message && (
            <Alert variant={submitMessage.variant}>{submitMessage.message}</Alert>
          )}
          <Button variant="primary" onClick={handleShowAddMovieModal}>
            Add Movie
          </Button>
          <Table striped bordered hover variant="dark">
            <thead>
              <tr>
                <th>#</th>
                <th>Name</th>
                <th>Genre</th>
                <th>URL</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {movies.map((movie, index) => (
                <tr key={movie._id}>
                  <td>{index + 1}</td>
                  <td>{movie.title}</td>
                  <td>{movie.genre}</td>
                  <td>
                    <a href={movie.image_url} target="_blank" rel="noopener noreferrer">
                      {movie.image_url.split("/").slice(-2)[0]}
                    </a>
                  </td>
                  <td>
                    <Button variant="outline-success">UPDATE</Button>{" "}
                    <Button variant="outline-danger" onClick={() => handleDeleteMovie(movie._id)}>
                      DELETE
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </>
      ) : (
        <>
          <h2>You are not authorized to use. You have to be a registered Admin and logged-in.</h2>
          <LinkContainer to="/Admin">
            <Nav.Link className="navbar-link active">
              <h5>Click Here To Login</h5>
            </Nav.Link>
          </LinkContainer>
        </>
      )}
      <Modal show={showAddMovieModal} onHide={handleCloseAddMovieModal}>
        <Modal.Header closeButton>
          <Modal.Title>Add Movie</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleAddMovieSubmit}>
            <Form.Group controlId="formMovieName">
              <Form.Label>Movie Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter movie name"
                name="movieName"
                value={formValues.movieName}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="formMovieGenre">
              <Form.Label>Genre</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter movie genre"
                name="movieGenre"
                value={formValues.movieGenre}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="formMovieURL">
              <Form.Label>Image URL</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter image URL"
                name="movieURL"
                value={formValues.movieURL}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
      <Footer />
    </Container>
  );
}

export default MyList;
